class Solution {
    public int longestValidParentheses(String s) {

        IntStack stack = new IntStack(1024);

        int maxLength = 0;

        /**
         * 主要针对这两种情况
         *      ()()()
         *      (()())
         */
        int start = -1;
        for (int i = 0; i < s.length(); i++) {
            switch (s.charAt(i)) {
                case '(':
                    stack.push(i);
                    break;
                case ')':
                    if (stack.isEmpty()) {
                        start = i;
                    } else {
                        stack.pop();
                        int length = stack.isEmpty() ? i - start : i - stack.peek();
                        maxLength = length > maxLength ? length : maxLength;
                    }
                    break;
            }
        }


        return maxLength;
    }

    static class IntStack {
        int[] ints = null;
        int i = 0; //指向当前空位

        IntStack(int size) {
            ints = new int[size];
        }

        void push(int c) {
            ints[i++] = c;
        }

        int pop() {
            if (i == 0) {
                return 0;
            }
            return ints[--i];
        }

        int peek() {
            return ints[i - 1];
        }

        public boolean isEmpty() {
            return i == 0;
        }
    }
}

class Solution2 {
    public int longestValidParentheses(String s) {
        if (s.isEmpty()) {
            return 0;
        }

        int[] dp = new int[s.length() + 1];
        dp[0] = 0;

        int maxLength = 0;
        for (int i = 0; i < s.length(); i++) {
            int p = i + 1;
            switch (s.charAt(i)) {
                case '(':
                    dp[p] = p;
                    break;
                case ')':
                    if (dp[p - 1] <= 0) {
                        dp[p] = -p;
                        int length = p + dp[p];
                        maxLength = length > maxLength ? length : maxLength;
                    } else {
                        dp[p] = dp[p - 1] - 1;
                    }
                    break;
            }
        }

        int end = dp.length - 1;
        if (dp[end] == end) {
            return maxLength;
        } else {
            int length = end + dp[end];
            return length > maxLength ? length : maxLength;
        }


    }
}

class Solution3 {
    public int longestValidParentheses(String s) {
        int maxans = 0;
        int dp[] = new int[s.length()];
        for (int i = 1; i < s.length(); i++) {
            if (s.charAt(i) == ')') {
                if (s.charAt(i - 1) == '(') {
                    dp[i] = (i >= 2 ? dp[i - 2] : 0) + 2;
                } else if (i - dp[i - 1] > 0 && s.charAt(i - dp[i - 1] - 1) == '(') {
                    dp[i] = dp[i - 1] + ((i - dp[i - 1]) >= 2 ? dp[i - dp[i - 1] - 2] : 0) + 2;
                }
                maxans = Math.max(maxans, dp[i]);
            }
        }
        return maxans;
    }
}

class Solution4 {
    public int longestValidParentheses(String s) {
        int d[] = new int[s.length() + 1];
        int maxLen = 0;
        d[0] = 0;

        for (int i = 0; i < s.length(); i++) {
            int p = i + 1;
            switch (s.charAt(i)) {
                case '(':
                    d[p] = p;
                    break;
                case ')':
                    if (d[p - 1] <= 0) {
                        d[p] = - p;
                    } else {
                        d[p] = d[d[p - 1] - 1];
                        int len = p - Math.abs(d[p]);
                        maxLen = len > maxLen ? len : maxLen;
                    }
                    break;
            }
        }

        return maxLen;
    }
}


